    (async () => {
      let res = await fetch(
        `https://excelinsure-backend-229kl.ondigitalocean.app/api/autocopytrade/${_id}`
      );
      let _data = await res.json();
      this.setState({ userAutoCopyTradedata: _data });
    })();
    (async () => {
      let response = await fetch(
        `https://excelinsure-backend-229kl.ondigitalocean.app/api/trade/user/${this.state.user.user.user._id}`
      );
      let user = await response.json();
      this.setState({
        user: user,
      });

      let a = { user: user };
      localStorage.setItem("user", JSON.stringify(a));
    })();
  };
  deleteAutoCopyTrade = (id, _userId) => {
    this.setState({ deleteLoading: true });
    axios
      .delete(
        `https://excelinsure-backend-229kl.ondigitalocean.app/api/autocopytrade/${id}`
      )
      .then(
        (response) => {
          this.callBackAutoTrade(_userId);
          message.success("successfully deleted auto-trade");
          this.setState({ deleteLoading: false });
        },
        (error) => {
          console.log(error);
          message.error("error deleting auto-trade");
        }
      );
  };
