                              </Form.Group>
                            </Col>
                          </Row>

                          <Row>
                            <Col xs={12} md={6}>
                              <Form.Group>
                                <Form.Control
                                  value={this.state.yourPassword}
                                  onChange={(e) => {
                                    this.setState({
                                      yourPassword: e.target.value,
                                    });
                                    console.log(
                                      this.state.yourPassword,
                                      "lllll"
                                    );
                                  }}
                                  type="password"
                                  placeholder="Your New Password"
                                  name="newPassword"
                                  id="yourPassword"
                                />
                              </Form.Group>
                            </Col>

                            <Col xs={12} md={6}>
                              <Form.Group>
                                <Form.Control
                                  value={this.state.yourPasswordComfirm}
                                  onChange={(e) => {
                                    this.setState({
                                      yourPasswordComfirm: e.target.value,
                                    });
                                    console.log(
                                      this.state.yourPasswordComfirm,
                                      "lllll"
                                    );
                                  }}
